import React, { Component } from 'react';
import './App.css';
import TextField from './components/TextField';
import SelectField from './components/SelectField'
import OrderDetails from './components/OrderDetails';
import CustomerDetails from './components/CustomerDetails';
import CurrencyDetails from './components/CurrencyDetails';
import AddressDetails from './components/AddressDetails';
import LocationDetails from './components/LocationDetails'

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      type: ["date", "text"],
      invoice: ["invoice1", "invoice2", "invoice3"],
      currency: ["USD", "EURO", "INR"],
      freight: ["FOB1", "FOB2", "FOB3"],
      shippingMethods: ["AirBorne", "Ship", "Train"],
      country: ["india", "USA", "NewZealand"]
    }
  }
  render() {
    return (
      <div className="App">
        <div className="title">Sales Order</div>
        <OrderDetails dateField={this.state.type[0]} textField={this.state.type[1]} invoiceDetails={this.state.invoice} />
        <CustomerDetails textField={this.state.type[1]} />
        <CurrencyDetails textField={this.state.type[1]} currencyOptions={this.state.currency} freightOptions={this.state.freight} shipMethod={this.state.shippingMethods} />
        <AddressDetails textField={this.state.type[1]} />
        <LocationDetails textField={this.state.type[1]} countryField={this.state.country} />
         {/* <div id="locationDetails">
           <span className="divTitle">city: </span><TextField type={this.state.type[1]} />
           <span className="divTitle">State: </span><TextField type={this.state.type[1]} />
           <span className="divTitle">Postal Code: </span><TextField type={this.state.type[1]} />
           <span className="divTitle">country: </span><div className="selectField">
             <SelectField invoiceDetails={this.state.country} />
           </div>
         </div> */}
      </div>
    );
  }
}

export default App;
